import React from "react";
import { Wifi } from "lucide-react";
import { ChartLine } from "lucide-react";
import { Gpu } from "lucide-react";
import {SignalHigh} from "lucide-react";
import { LayoutDashboard } from "lucide-react";

export default function Technology() {
  return (
    <section id="technology" className="bg-green-50 py-20 px-6">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-4xl font-bold text-center text-green-800 mb-10">
          Powered by Mudhumeni Agentic Logic
        </h2>
        <div className="grid md:grid-cols-2 gap-12 items-center">
          <div>
            <ul className="space-y-4 text-gray-700">
              <li>✅ IoT sensors for temperature, humidity, and soil moisture</li>
              <li>✅ AI vision & acoustic analysis for pest and disease detection</li>
              <li>✅ Edge AI (Jetson) for fast, private processing</li>
              <li>✅ 4G/5G connectivity for remote monitoring</li>
              <li>✅ Dashboard visualization with alerts and growth analytics</li>
            </ul>
          </div>
          <div className="relative">
            <img
              src="/greenhouse-ai.jpg"
              alt="AI in Greenhouse"
              className="rounded-lg shadow-lg"
            />
            <div className="absolute inset-0 bg-gradient-to-tr from-green-600/20 to-transparent rounded-lg" />
          </div>
        </div>
      </div>
    </section>
  );
}
