import React from "react";

export default function Impact() {
  const stats = [
    { label: "Yield Increase", value: "50%" },
    { label: "Reduced Waste", value: "40%" },
    { label: "Automation", value: "24/7" },
  ];

  return (
    <section id="impact" className="bg-white py-20">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-green-800 mb-8">Impact & Benefits</h2>
        <p className="text-gray-700 mb-12 max-w-3xl mx-auto">
          M.A.L transforms reactive farming into a predictive and autonomous system,
          boosting profitability and sustainability.
        </p>
        <div className="grid md:grid-cols-3 gap-8">
          {stats.map((s, i) => (
            <div key={i} className="bg-green-100 rounded-lg p-8 shadow-md">
              <p className="text-4xl font-bold text-green-800">{s.value}</p>
              <p className="text-gray-700 mt-2 uppercase tracking-wide">{s.label}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
